function updateSeats(){$.get("http://api.musikquiz-online.de/getSeats?sessionId=1234",function(t){$(".seat#l").text(t.l),$(".seat#m").text(t.m),$(".seat#r").text(t.r)})}$("#button1").click(function(){$.post("http://api.musikquiz-online.de/storeSeat?sessionId=1234&name="+$("#name-input").val()+"&seat=1")}),$("#button2").click(function(){console.log($("#name-input").val()),$.post("http://api.musikquiz-online.de/storeSeat?sessionId=1234&name="+$("#name-input").val()+"&seat=2")}),$("#resetGame").click(function(){$.post("http://api.musikquiz-online.de/resetSeats")}),window.setInterval(function(){updateSeats()},500);
