function updateScoreboard(){$.get("/api/scoreboard",(function(t){$("#scoreboard-body").html(generateTable(t))}))}function generateTable(t){let o="";return t.map((t,e)=>{o+='<tr><td style="border-bottom-width: 1px; font-weight: bolder">'+(e+1).toString()+"</td><td>"+t.score+'</td><td style="border-right: 1px none rgb(128,128,128) ;border-bottom-width: 2px;border-bottom-color: rgb(128,128,128);">'+t.name+'</td><td style="border-right: 1px none rgb(128,128,128) ;border-bottom-width: 2px;border-bottom-color: rgb(128,128,128);">'+`<button class="btn btn-danger" type="button" style="width: 40px; margin-right: 10px" onclick="deleteTeam('${t.id}')"><i class="fa fa-trash-o"></i></button><div role="group" class="btn-group" style="height: 40px;"><button class="btn btn-warning" type="button" style="width: 40px;" onclick="retractPoint('${t.id}')"><i class="fa fa-minus" style="color: rgb(255,255,255);"></i></button><button class="btn btn-success" type="button" style="width: 40px;" onclick="addPoint('${t.id}')"><i class="fa fa-plus"></i></button></div></td></tr>`}),o}function deleteTeam(t){confirm("Willst du dieses Team wirklich löschen?")&&$.ajax({url:"/api/scoreboard/team/"+t,type:"DELETE",success:function(t){updateScoreboard()}})}function addPoint(t){$.get("api/scoreboard/addPoint/"+t,(function(t){updateScoreboard()}))}function retractPoint(t){$.get("api/scoreboard/retractPoint/"+t,(function(t){updateScoreboard()}))}function resetScoreboard(){confirm("Willst du das Scoreboard wirklich zurücksetzen?")&&$.get("api/scoreboard/reset",t=>{updateScoreboard()})}window.setInterval((function(){updateScoreboard()}),1e3),$("#addTeam").click((function(){$.post("/api/scoreboard/team",{name:$("#team-name-input").val()}),$("#team-name-input").val("")}));