function updateScoreboard(){$.get("/api/scoreboard",(function(t){let o="";t.map((t,r)=>{o+='<tr><td style="border-bottom-width: 1px; font-weight: bolder">'+(r+1).toString()+"</td><td>"+t.score+'</td><td style="border-right: 1px none rgb(128,128,128) ;border-bottom-width: 2px;border-bottom-color: rgb(128,128,128);">'+t.name+"</td></tr>"}),$("#scoreboard-body").html(o)}))}window.setInterval((function(){updateScoreboard()}),1e3);