function updateSeats(){$.get("/api/seats?sessionId=1234",(function(t){$(".seat#l").text(t.l),$(".seat#m").text(t.m),$(".seat#r").text(t.r)}))}$("#button1").click((function(){$.post("/api/seats/store?sessionId=1234&name="+$("#name-input").val()+"&seat=1")})),$("#button2").click((function(){$.post("/api/seats/store?sessionId=1234&name="+$("#name-input").val()+"&seat=2")})),$("#resetGame").click((function(){$.post("/api/seats/reset")})),window.setInterval((function(){updateSeats()}),500);